sysauto_script.script:
dtmNow = new GlideDateTime();
dtmTomorrow = new GlideDateTime(dtmNow);
dtmTomorrow.addDaysUTC(1);

gs.info('Now = ' + dtmNow);
gs.info('Tomorrow = ' + dtmTomorrow);

grNITasks = new GlideRecord('x_58872_needit_needit_task');
grNITasks.addQuery('due_date','>=',dtmNow);
grNITasks.addQuery('due_date', '<=',dtmTomorrow);
grNITasks.addQuery('state', '!=', 3);
grNITasks.query();
while(grNITasks.next()){
	gs.info('NeedIt task due tomorrow: ' + grNITasks.getValue('number'));
	gs.eventQueue('x_58872_needit.overdueNeedItTask', grNITasks, grNITasks.getValue('number'), grNITasks.getValue('short_description'),'aps_queue');
}
